/**
 * @module chat-header
 *
 * Sticky header components for the chat UI. Compose `ChatHeaderMain`,
 * `ChatHeaderAddon`, `ChatHeaderAvatar`, and `ChatHeaderButton` inside
 * a `ChatHeader` container to build the header layout.
 *
 * Typical structure:
 * ```
 * ChatHeader
 * ‚îú‚îÄ‚îÄ ChatHeaderAddon          ‚Üê left-side items (avatar, back button)
 * ‚îú‚îÄ‚îÄ ChatHeaderMain           ‚Üê center content (takes remaining space)
 * ‚îî‚îÄ‚îÄ ChatHeaderAddon          ‚Üê right-side items (action buttons)
 * ```
 */

import * as React from "react";
import { cn } from "@/lib/utils";
import {
  AvatarFallbackProps,
  AvatarImageProps,
  AvatarProps,
} from "@radix-ui/react-avatar";
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/registry/new-york/ui/avatar";
import { Button } from "@/registry/new-york/ui/button";

export interface ChatHeaderProps extends React.ComponentProps<"div"> {
  children?: React.ReactNode;
}

/**
 * Sticky header container for the chat. Renders as a flex row pinned
 * to the top of the `Chat` container.
 *
 * @example
 * ```tsx
 * <ChatHeader className="border-b">
 *   <ChatHeaderAddon>
 *     <ChatHeaderAvatar src="/avatar.png" alt="@user" fallback="AS" />
 *   </ChatHeaderAddon>
 *   <ChatHeaderMain>
 *     <span className="font-medium">Ann Smith</span>
 *   </ChatHeaderMain>
 *   <ChatHeaderAddon>
 *     <ChatHeaderButton><PhoneIcon /></ChatHeaderButton>
 *     <ChatHeaderButton><MoreHorizontalIcon /></ChatHeaderButton>
 *   </ChatHeaderAddon>
 * </ChatHeader>
 * ```
 */
export function ChatHeader({ children, className, ...props }: ChatHeaderProps) {
  return (
    <div
      className={cn(
        "sticky top-0 z-10 p-2 bg-background flex items-center gap-2",
        className,
      )}
      {...props}
    >
      {children}
    </div>
  );
}

export interface ChatHeaderMainProps extends React.ComponentProps<"div"> {
  children?: React.ReactNode;
}

/**
 * Primary content area of the header. Uses `flex-1` to take remaining
 * horizontal space between `ChatHeaderAddon` groups.
 *
 * @example
 * ```tsx
 * <ChatHeaderMain>
 *   <span className="font-medium">Ann Smith</span>
 *   <span className="flex-1 grid">
 *     <span className="text-sm font-medium truncate">Front-end developer</span>
 *   </span>
 * </ChatHeaderMain>
 * ```
 */
export function ChatHeaderMain({
  children,
  className,
  ...props
}: ChatHeaderMainProps) {
  return (
    <div className={cn("flex-1 flex items-center gap-2", className)} {...props}>
      {children}
    </div>
  );
}

export interface ChatHeaderAddonProps extends React.ComponentProps<"div"> {
  children?: React.ReactNode;
}

/**
 * Groups supplementary items (avatars, buttons, inputs) on either side
 * of the header. Place one before `ChatHeaderMain` for the left side
 * and one after for the right side.
 *
 * @example
 * ```tsx
 * // Left side
 * <ChatHeaderAddon>
 *   <ChatHeaderAvatar src="/avatar.png" alt="@user" fallback="AS" />
 * </ChatHeaderAddon>
 *
 * // Right side
 * <ChatHeaderAddon>
 *   <ChatHeaderButton><PhoneIcon /></ChatHeaderButton>
 *   <ChatHeaderButton><VideoIcon /></ChatHeaderButton>
 * </ChatHeaderAddon>
 * ```
 */
export function ChatHeaderAddon({
  children,
  className,
  ...props
}: ChatHeaderAddonProps) {
  return (
    <div className={cn("flex items-center gap-2", className)} {...props}>
      {children}
    </div>
  );
}

export interface ChatHeaderAvatarProps extends AvatarProps {
  className?: string;
  /** Image URL for the avatar. */
  src?: AvatarImageProps["src"];
  /** Alt text for the avatar image. */
  alt?: string;
  /** Fallback content shown while the image loads or if it fails (e.g. initials). */
  fallback?: React.ReactNode;
  /** Additional props forwarded to the inner `AvatarImage`. */
  imageProps?: AvatarImageProps;
  /** Additional props forwarded to the inner `AvatarFallback`. */
  fallbackProps?: AvatarFallbackProps;
}

/**
 * Avatar component sized for the header. Built on Radix UI Avatar
 * primitives with rounded styling.
 *
 * @example
 * ```tsx
 * <ChatHeaderAvatar
 *   src="https://example.com/avatar.png"
 *   alt="@annsmith"
 *   fallback="AS"
 * />
 * ```
 */
export function ChatHeaderAvatar({
  className,
  src,
  alt,
  fallback,
  imageProps,
  fallbackProps,
  ...props
}: ChatHeaderAvatarProps) {
  return (
    <Avatar className={cn("rounded-full", className)} {...props}>
      <AvatarImage src={src} alt={alt} {...imageProps} />
      {fallback && (
        <AvatarFallback {...fallbackProps}>{fallback}</AvatarFallback>
      )}
    </Avatar>
  );
}

export interface ChatHeaderButtonProps extends React.ComponentProps<
  typeof Button
> {
  children?: React.ReactNode;
}

/**
 * Pre-styled ghost icon button for header actions (phone, video, menu, etc.).
 * Uses `variant="ghost"` and `size="icon-sm"`.
 *
 * @example
 * ```tsx
 * <ChatHeaderButton>
 *   <MoreHorizontalIcon />
 * </ChatHeaderButton>
 * ```
 */
export function ChatHeaderButton({
  children,
  className,
  ...props
}: ChatHeaderButtonProps) {
  return (
    <Button variant="ghost" size="icon-sm" className={cn(className)} {...props}>
      {children}
    </Button>
  );
}

/**
 * @module chat-toolbar
 *
 * Sticky bottom input area for message composition. Compose
 * `ChatToolbarTextarea`, `ChatToolbarAddon`, and `ChatToolbarButton`
 * inside a `ChatToolbar` container.
 *
 * Typical structure:
 * ```
 * ChatToolbar
 * ‚îú‚îÄ‚îÄ ChatToolbarAddon (align="inline-start")  ‚Üê left button(s)
 * ‚îÇ   ‚îî‚îÄ‚îÄ ChatToolbarButton
 * ‚îú‚îÄ‚îÄ ChatToolbarTextarea                       ‚Üê auto-growing input
 * ‚îî‚îÄ‚îÄ ChatToolbarAddon (align="inline-end")     ‚Üê right button(s)
 *     ‚îî‚îÄ‚îÄ ChatToolbarButton (√óN)
 * ```
 *
 * The `align` prop on `ChatToolbarAddon` controls position via CSS
 * `order`:
 * - `"inline-start"` ‚Üí left of the textarea
 * - `"inline-end"` ‚Üí right of the textarea
 * - `"block-start"` ‚Üí full-width row above the textarea
 * - `"block-end"` ‚Üí full-width row below the textarea
 */

"use client";

import * as React from "react";
import { cn } from "@/lib/utils";
import { Textarea } from "@/registry/new-york/ui/textarea";
import { Button } from "@/registry/new-york/ui/button";

export interface ChatToolbarProps extends React.ComponentProps<"div"> {
  children?: React.ReactNode;
}

/**
 * Sticky bottom container for the message input and action buttons.
 * Renders a bordered, rounded inner wrapper with flex-wrap layout.
 *
 * @example
 * ```tsx
 * <ChatToolbar>
 *   <ChatToolbarAddon align="inline-start">
 *     <ChatToolbarButton><PlusIcon /></ChatToolbarButton>
 *   </ChatToolbarAddon>
 *
 *   <ChatToolbarTextarea
 *     value={message}
 *     onChange={(e) => setMessage(e.target.value)}
 *     onSubmit={() => handleSendMessage()}
 *   />
 *
 *   <ChatToolbarAddon align="inline-end">
 *     <ChatToolbarButton><GiftIcon /></ChatToolbarButton>
 *     <ChatToolbarButton><SendIcon /></ChatToolbarButton>
 *   </ChatToolbarAddon>
 * </ChatToolbar>
 * ```
 */
export function ChatToolbar({
  children,
  className,
  ...props
}: ChatToolbarProps) {
  return (
    <div
      className={cn("sticky bottom-0 p-2 pt-0 bg-background", className)}
      {...props}
    >
      <div
        className={cn(
          "border rounded-md py-2 px-3",
          "flex flex-wrap items-start gap-x-2",
        )}
      >
        {children}
      </div>
    </div>
  );
}

/** Modifier key that allows inserting a new line instead of submitting */
const NEWLINE_MODIFIER_KEY = "shiftKey" as const;

export interface ChatToolbarTextareaProps extends React.ComponentProps<
  typeof Textarea
> {
  /** Called when the user presses Enter (without Shift). Use this to trigger message sending. */
  onSubmit?: () => void;
}

/**
 * Auto-growing textarea with built-in submit handling.
 *
 * - **Enter** ‚Üí calls `onSubmit()`
 * - **Shift+Enter** ‚Üí inserts a new line
 *
 * Accepts all standard `<textarea>` / shadcn `Textarea` props
 * (`value`, `onChange`, `placeholder`, etc.).
 *
 * @example
 * ```tsx
 * const [message, setMessage] = useState("");
 *
 * <ChatToolbarTextarea
 *   value={message}
 *   onChange={(e) => setMessage(e.target.value)}
 *   onSubmit={() => {
 *     sendMessage(message);
 *     setMessage("");
 *   }}
 * />
 * ```
 */
export function ChatToolbarTextarea({
  className,
  onSubmit,
  onKeyDown,
  ...props
}: ChatToolbarTextareaProps) {
  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e[NEWLINE_MODIFIER_KEY]) {
      e.preventDefault();
      onSubmit?.();
    }
    onKeyDown?.(e);
  };

  return (
    <div className="flex-1 min-w-0 order-2 grid">
      <Textarea
        id="toolbar-input"
        placeholder="Type your message..."
        className={cn(
          "h-fit min-h-10 max-h-30 px-1 @md/chat:text-base",
          "border-none shadow-none focus-visible:border-none focus-visible:ring-0 placeholder:whitespace-nowrap resize-none",
          className,
        )}
        rows={1}
        onKeyDown={handleKeyDown}
        {...props}
      />
    </div>
  );
}

const chatToolbarAddonAlignStyles = {
  "inline-start": "order-1",
  "inline-end": "order-3",
  "block-start": "order-0 w-full",
  "block-end": "order-4 w-full",
};

export interface ChatToolbarAddonProps extends React.ComponentProps<"div"> {
  children?: React.ReactNode;
  /**
   * Position of this addon relative to the textarea.
   * - `"inline-start"` ‚Äî left of the textarea (default)
   * - `"inline-end"` ‚Äî right of the textarea
   * - `"block-start"` ‚Äî full-width row above the textarea
   * - `"block-end"` ‚Äî full-width row below the textarea
   */
  align?: "inline-start" | "inline-end" | "block-start" | "block-end";
}

/**
 * Groups action buttons at a specific position within the toolbar.
 * Use the `align` prop to control placement relative to the textarea.
 *
 * @example
 * ```tsx
 * // Left side
 * <ChatToolbarAddon align="inline-start">
 *   <ChatToolbarButton><PlusIcon /></ChatToolbarButton>
 * </ChatToolbarAddon>
 *
 * // Right side
 * <ChatToolbarAddon align="inline-end">
 *   <ChatToolbarButton><SendIcon /></ChatToolbarButton>
 * </ChatToolbarAddon>
