  ChatEventBody,
  ChatEventContent,
  ChatEventTime,
} from "@/registry/new-york/chat/chat-event";

export function AdditionalMessage({
  content,
  timestamp,
}: {
  content: ReactNode;
  timestamp: number;
}) {
  return (
    <ChatEvent className="hover:bg-accent group">
      <ChatEventAddon>
        <ChatEventTime
          timestamp={timestamp}
          format="time"
          className="text-right text-[8px] @md/chat:text-[10px] group-hover:visible invisible"
        />
      </ChatEventAddon>
      <ChatEventBody>
        <ChatEventContent>{content}</ChatEventContent>
      </ChatEventBody>
    </ChatEvent>
  );
}

import { Separator } from "@/components/ui/separator";
import { cn } from "@/lib/utils";
import { ChatEvent, ChatEventTime } from "@/registry/new-york/chat/chat-event";

export function DateItem({
  timestamp,
  className,
}: {
  timestamp: number;
  className?: string;
}) {
  return (
    <ChatEvent className={cn("items-center gap-1", className)}>
      <Separator className="flex-1" />
      <ChatEventTime
        timestamp={timestamp}
        format="longDate"
        className="font-semibold min-w-max"
      />
      <Separator className="flex-1" />
    </ChatEvent>
  );
}

